package example;

public class Casting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/// Upcasting converting - lower data data type into larger
		int a= 10;
		long b =  a;
		System.out.println(b);
		
		// Downcasting  -- larger data type into smaller
		
		double f = 894456.78;
		float l = (float) f;
		System.out.println(l);
		

	}

}
