package example1;

public class EnhancedForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int arr[] = {2, 4,6,8};
    int e = 0;
    int z= arr.length;
	
    for (int i= 0 ; i< arr.length ; i++)
    {
    	 e = arr[i];
    	 System.out.print(e + " ");
    }
    System.out.println("");
    System.out.println(z);
   
	}
	

}
