package pack1;

public class UnaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a= 20;
		int b= 10;
		
	int result = a++;
	int result1= ++a;
	int result2 = --b;
	int result3 = b--; 
	System.out.println(result);
	System.out.println(result1);
	System.out.println(result2);
	System.out.println(result3);
	
	}

}
